// https://www.freecodecamp.org/chinese/news/how-to-consume-rest-apis-in-react/
import {useEffect, useState} from "react";

function App() {
  const [userList, setUserList] = useState([]);
  const [commentArr, setCommentArr] = useState([]);
  const [formData, setFormData] = useState({
    id: "",
    name: "",
    year: 0,
  });
  const userURL = "/user";
  const commentURL = "/comment";

  useEffect(() => {
    const fetchUserData = async () => {
      const res = await fetch(userURL);
      const resData = await res.json();
      const userData = resData.data;
      console.log(userData);
      setUserList(userData);
    };

    fetchUserData();
  }, []);

  const addUser = async () => {
    const res = await fetch(userURL, {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(formData),
    });
    const resData = await res.json();
    const userData = resData.data;
    console.log(userData);
    setUserList(userData);
  };

  async function handleSubmit(e) {
    e.preventDefault();
    setFormData({
      id: "",
      name: "",
      year: 0,
    });

    await fetch(userURL)
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
  }

  const renderedUserItem = userList.map((user, index) => (
    <tr key={index}>
      <td>{user.id}</td>
      <td>{user.name}</td>
      <td>{user.year}</td>
    </tr>
  ));

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <label>ID: </label>
        <input
          type="text"
          placeholder="Input ID"
          value={formData.id}
          onChange={(e) =>
            setFormData({
              ...formData,
              id: e.target.value,
            })
          }
        />
        <br />
        <label>Name: </label>
        <input
          type="text"
          placeholder="Input name"
          value={formData.name}
          onChange={(e) =>
            setFormData({
              ...formData,
              name: e.target.value,
            })
          }
        />
        <br />
        <label>Year: </label>
        <input
          type="text"
          placeholder="Input Year"
          value={formData.year}
          onChange={(e) =>
            setFormData({
              ...formData,
              year: Number(e.target.value) || 0,
            })
          }
        />
        <br />
        <button type="submit">Add</button>
      </form>

      <div>
        <div>User List:</div>
        <table border={1}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Year</th>
            </tr>
          </thead>
          <tbody>{renderedUserItem}</tbody>
        </table>
      </div>
    </div>
  );
}

export default App;

