// https://www.freecodecamp.org/chinese/news/how-to-consume-rest-apis-in-react/
import {useEffect, useState} from "react";
// import FormPage from "./components/FormPage";
// import UserList from "./components/UserList";

function App() {
  const [userList, setUserList] = useState([]);
  const [commentArr, setCommentArr] = useState([]);
  const [formData, setFormData] = useState({
    id: "",
    name: "",
    year: 0,
  });
  const userURL = "/user";
  const commentURL = "/comment";

  useEffect(() => {
    fetch(userURL)
      .then((res) => res.json())
      .then((userData) => {
        const usersData = userData.data;
        console.log({usersData});
      });
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    setFormData({
      id: "",
      name: "",
      year: 0,
    });

    await fetch(userURL, {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(formData),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <label>ID: </label>
        <input
          type="text"
          placeholder="Input ID"
          value={formData.id}
          onChange={(e) =>
            setFormData({
              ...formData,
              id: e.target.value,
            })
          }
        />
        <br />
        <label>Name: </label>
        <input
          type="text"
          placeholder="Input name"
          value={formData.name}
          onChange={(e) =>
            setFormData({
              ...formData,
              name: e.target.value,
            })
          }
        />
        <br />
        <label>Year: </label>
        <input
          type="text"
          placeholder="Input Year"
          value={formData.year}
          onChange={(e) =>
            setFormData({
              ...formData,
              year: Number(e.target.value) || 0,
            })
          }
        />
        <br />
        <button type="submit">Add</button>
      </form>

      {/* <UserList userList={userArr} /> */}
    </div>
  );
}

export default App;
