import {useState} from "react";

function FormPage(props) {
  const [userObj, setUserObj] = useState({
    id: "",
    name: "",
    year: 0,
  });
  const userURL = "http://localhost:3333/user";

  async function handleSubmit(event) {
    event.preventDefault();
    useState({
      id: "",
      name: "",
      year: 0,
    });

    await fetch(userURL, {
      method: "POST",
      body: userObj,
    });
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>ID: </label>
      <input
        type="text"
        placeholder="Input ID"
        value={userObj.id}
        onChange={(e) => {
          setUserObj({
            ...userObj,
            id: e.target.value,
          });
        }}
      />
      <br />
      <label>Name: </label>
      <input
        type="text"
        placeholder="Input name"
        value={userObj.name}
        onChange={(e) => {
          setUserObj({
            ...userObj,
            id: e.target.value,
          });
        }}
      />
      <br />
      <label>Year: </label>
      <input
        type="text"
        placeholder="Input Year"
        value={year}
        onChange={(e) => setYear(Number(e.target.value) || 0)}
      />
      <br />
      <button type="submit">Add</button>
    </form>
  );
}

export default FormPage;
